sp_widget.client_script:
api.controller = function($window, $location) {
  var c = this;

  // Set current page for sidebar highlight
  c.currentPage = $location.search().id || 'track_my_request_page';

  c.navigate = function(pageId) {
    if (pageId === 'art') {
      $window.location.href = '/art';
    } else {
      $window.location.href = '/art?id=' + pageId;
    }
  };

  c.viewDetails = function(sysId) {
    // Navigate to the form view of the specific request
    $window.location.href = '/art?id=form&table=x_1862743_asset_re_asset_request_table&sys_id=' + sysId;
  };
};

