sp_widget.script:
(function() {
    var user = gs.getUser();
    data.user_first_name = user.getFirstName();
    data.user_initials = (user.getFirstName().charAt(0) + user.getLastName().charAt(0)).toUpperCase();
    data.project_name = "IT Asset Management";

    data.requests = [];
    var userId = gs.getUserID();
    var table = 'x_1862743_asset_re_asset_request_table';

    var gr = new GlideRecord(table);
    // Filter by opened_by since the table extends Task
    gr.addQuery('opened_by', userId); 
    gr.orderByDesc('sys_created_on');
    gr.query();

    while (gr.next()) {
        var obj = {};
        obj.number = gr.getDisplayValue('number');
        obj.asset = gr.request_asset.getDisplayValue();
        obj.quantity = gr.getValue('quantity');
        obj.state = gr.getDisplayValue('state');
        obj.approval = gr.getValue('approval_status') || gr.getValue('approval'); 
        obj.sys_id = gr.getUniqueValue();
        obj.date = gr.getDisplayValue('sys_created_on');

        // Map status classes
        obj.statusClass = 'status-pending';
        if (obj.approval == 'approved') obj.statusClass = 'status-approved';
        if (obj.approval == 'rejected') obj.statusClass = 'status-rejected';

        data.requests.push(obj);
    }
})();
