sp_widget.script:
(function() {
    data.stats = {
        total_requests: 0,
        total_cost: 0,
        top_asset: "",
        pending_count: 0
    };
    data.chartData = [];
    var table = 'x_1862743_asset_re_asset_request_table';

    // 1. Get Total Requests & Sum of Costs
    var ga = new GlideAggregate(table);
    ga.addAggregate('COUNT');
    ga.addAggregate('SUM', 'total_cost');
    ga.addQuery('approval', 'approved'); // Only count realized costs
    ga.setGroup(false);
    ga.query();
    if (ga.next()) {
        data.stats.total_requests = ga.getAggregate('COUNT');
        data.stats.total_cost = ga.getAggregate('SUM', 'total_cost') || 0;
    }

    // 2. Get Top Requested Asset
    var topAsset = new GlideAggregate(table);
    topAsset.addAggregate('COUNT', 'request_asset');
    topAsset.orderByAggregate('COUNT', 'request_asset');
    topAsset.query();
    if (topAsset.next()) {
        data.stats.top_asset = topAsset.request_asset.getDisplayValue();
    }

    // 3. Prepare Chart Data (Requests by State)
    var stateCount = new GlideAggregate(table);
    stateCount.addAggregate('COUNT', 'approval');
    stateCount.groupBy('approval');
    stateCount.query();
    while (stateCount.next()) {
        data.chartData.push({
            label: stateCount.approval.getDisplayValue() || 'Unknown',
            value: stateCount.getAggregate('COUNT', 'approval')
        });
    }
})();
