sp_widget.script:
(function() {
    var user = gs.getUser();
    data.user_first_name = user.getFirstName();
    data.user_initials = (user.getFirstName().charAt(0) + user.getLastName().charAt(0)).toUpperCase();
    data.project_name = "IT Asset Management";

    data.stats = {
        total_requests: 0,
        total_cost: 0,
        top_asset: "None"
    };
    data.chartData = [];

    var requestTable = 'x_1862743_asset_re_asset_request_table';
    var catalogTable = 'x_1862743_asset_re_asset_catalog_table';

    // 1. NEW LOGIC: Calculate Total Inventory Value (Catalog Table)
    // We iterate through all assets to multiply Quantity * Cost
    var totalValue = 0;
    var grInventory = new GlideRecord(catalogTable);
    grInventory.addActiveQuery();
    grInventory.query();
    while (grInventory.next()) {
        var qty = parseInt(grInventory.getValue('available_quantity')) || 0;
        var cost = parseFloat(grInventory.getValue('unit_cost')) || 0;
        totalValue += (qty * cost);
    }
    data.stats.total_cost = totalValue.toLocaleString('en-GB', {minimumFractionDigits: 2});

    // 2. Get Total Requests count (Request Table)
    var gaReq = new GlideAggregate(requestTable);
    gaReq.addAggregate('COUNT');
    gaReq.setGroup(false);
    gaReq.query();
    if (gaReq.next()) {
        data.stats.total_requests = parseInt(gaReq.getAggregate('COUNT')) || 0;
    }

    // 3. Get Top Requested Asset (Request Table)
    var topAsset = new GlideAggregate(requestTable);
    topAsset.addAggregate('COUNT', 'request_asset');
    topAsset.orderByAggregate('COUNT', 'request_asset');
    topAsset.query();
    if (topAsset.next()) {
        data.stats.top_asset = topAsset.request_asset.getDisplayValue();
    }

    // 4. Prepare Chart Data (Requests by Approval Status)
    var stateCount = new GlideAggregate(requestTable);
    stateCount.addAggregate('COUNT', 'approval_status');
    stateCount.groupBy('approval_status');
    stateCount.query();
    while (stateCount.next()) {
        data.chartData.push({
            label: stateCount.approval_status.getDisplayValue() || 'Pending',
            value: parseInt(stateCount.getAggregate('COUNT', 'approval_status'))
        });
    }

    // 5. Calculate Success Rate Percentage
    var approvedCount = 0;
    data.chartData.forEach(function(item) {
        if(item.label.toLowerCase() == 'approved') approvedCount = item.value;
    });
    data.success_rate = (data.stats.total_requests > 0) ? Math.round((approvedCount / data.stats.total_requests) * 100) : 0;

})();
