sp_widget.client_script:
api.controller = function() {
    var c = this;

    c.goToCatalog = function() {
        window.location.href = "?id=asset_catalog";
    };

    c.goToRequest = function() {
        window.location.href = "?id=asset_request";
    };

    c.goToMyRequests = function() {
        window.location.href = "?id=my_requests";
    };

    c.goToDashboard = function() {
        window.location.href = "?id=asset_dashboard";
    };

    c.goToSupport = function() {
        window.location.href = "?id=support";
    };
};

