sp_widget.client_script:
api.controller = function ($location) {
    var c = this;

    // Navigate to a page without parameters
    c.navigate = function (pageId) {
        $location.search({
            id: pageId
        });
    };

    // Open details page with sys_id
    c.viewDetails = function (sysId) {
        if (!sysId) {
            return;
        }

        $location.search({
            id: 'view_tracking_details_form_page',
            sys_id: sysId
        });
    };

    // Go back to tracking page and clear sys_id
    c.goBack = function () {
        $location.search({
            id: 'track_my_request_page'
        });
    };
};

