sp_widget.script:
(function () {
    data.details = {};

    // 1. Read sys_id from URL
    var requestSysId = $sp.getParameter('sys_id');

    if (!requestSysId) {
        data.details.number = "No Sys_ID Provided";
        data.details.asset = "Select a record from Tracking";
        return;
    }

    // 2. Query the Asset Request Table
    var gr = new GlideRecord('x_1862743_asset_re_asset_request_table');
    if (gr.get(requestSysId)) {

        // ðŸ”¹ Dot-walking: request_asset.unit_cost 
        // This goes from the Request table -> Catalog table -> Unit Cost field
        var unitCostValue = gr.request_asset.unit_cost.getDisplayValue();

        data.details = {
            number: gr.getDisplayValue('number'),
            asset: gr.getDisplayValue('request_asset'),
            location: gr.getDisplayValue('delivery_location'),
            urgency: gr.getDisplayValue('urgency'),

            // Fixed Unit Cost via Dot-Walking
            unit_cost: unitCostValue, 

            quantity: gr.getValue('quantity'),
            total_cost: gr.getDisplayValue('total_cost'),

            // Ensure this field name matches your table (delivery_date vs expected_delivery)
            delivery_date: gr.getDisplayValue('delivery_date') || gr.getDisplayValue('expected_delivery'),

            status: gr.getDisplayValue('approval_status'),
            justification: gr.getValue('business_justification')
        };
    } else {
        data.details.number = "Invalid Request";
    }

    data.project_name = "IT Asset Management";
    data.user_first_name = gs.getUser().getFirstName() || "Admin";
    data.user_initials = data.user_first_name.charAt(0);

})();
