sp_widget.client_script:
api.controller = function($window, $location) {
  var c = this;
  c.selectedCat = 'All';

  // Set current page for sidebar highlight
  c.currentPage = $location.search().id || 'browse_and_request_page';

  // Navigation Logic for Sidebar
  c.navigate = function(pageId) {
    if (pageId === 'art') {
      $window.location.href = '/art';
    } else {
      $window.location.href = '/art?id=' + pageId;
    }
  };

  // Catalog Filtering
  c.setFilter = function(cat) {
    c.selectedCat = cat;
  };

  c.categoryFilter = function(item) {
    if (c.selectedCat === 'All') return true;
    return item.category === c.selectedCat;
  };

  // Redirect to Request Form
  c.selectAsset = function(assetID) {
    var table = 'x_1862743_asset_re_asset_request_table';
    var url = '?id=form&table=' + table + '&sys_id=-1&view=sp&query=request_asset=' + assetID;
    $location.url(url);
  };
};

