sp_widget.script:
(function() {
    var user = gs.getUser();
    data.user_first_name = user.getFirstName();
    data.user_initials = (user.getFirstName().charAt(0) + user.getLastName().charAt(0)).toUpperCase();
    data.project_name = "IT Asset Management";

    data.assets = [];
    data.categories = [];
    var catalogTable = 'x_1862743_asset_re_asset_catalog_table';

    // 1. Get Categories
    var catGr = new GlideAggregate(catalogTable);
    catGr.addAggregate('COUNT', 'asset_category');
    catGr.query();
    while (catGr.next()) {
        var cat = catGr.getValue('asset_category');
        if (cat) data.categories.push(cat);
    }

    // 2. Get All Assets
    var gr = new GlideRecord(catalogTable);
    gr.addActiveQuery();
    gr.query();
    while (gr.next()) {
        var asset = {};
        asset.name = gr.getValue('asset_name');
        asset.description = gr.getValue('asset_description');
        asset.cost = gr.getValue('unit_cost');
        asset.qty = gr.getValue('available_quantity');
        asset.category = gr.getValue('asset_category');
        asset.sys_id = gr.getUniqueValue();

        if (gr.image) {
            asset.image = '/' + gr.image.getDisplayValue(); 
        } else {
            asset.image = "/images/service_catalog/generic_small.gif";
        }
        data.assets.push(asset);
    }
})();
