sp_widget.client_script:
api.controller = function($window, $location) {
  var c = this;

  c.currentPage = 'my_assets_page';

  c.navigate = function(pageId) {
    if (pageId === 'art') {
      $window.location.href = '/art';
    } else {
      $window.location.href = '/art?id=' + pageId;
    }
  };

  c.reportIssue = function(assetId) {
    // Redirect to incident form or support page
    $window.location.href = '/art?id=sc_cat_item&sys_id=id_of_your_incident_item';
  };
};

