sp_widget.script:
(function() {
    var user = gs.getUser();
    data.user_first_name = user.getFirstName();
    data.user_initials = (user.getFirstName().charAt(0) + user.getLastName().charAt(0)).toUpperCase();
    data.project_name = "IT Asset Management";

    data.assets = [];
    var userId = gs.getUserID();

    // Change this to your actual inventory table name
    // Standard ServiceNow uses 'alm_asset' or 'alm_hardware'
    var assetTable = 'alm_hardware'; 

    var gr = new GlideRecord(assetTable);
    gr.addQuery('assigned_to', userId); 
    gr.addQuery('install_status', '1'); // Typically '1' means 'In Use'
    gr.orderByDesc('assigned');
    gr.query();

    while (gr.next()) {
        var obj = {};
        obj.tag = gr.getValue('asset_tag');
        obj.model = gr.model.getDisplayValue();
        obj.serial = gr.getValue('serial_number');
        obj.category = gr.model_category.getDisplayValue();
        obj.assigned_date = gr.getDisplayValue('assigned');
        obj.sys_id = gr.getUniqueValue();

        data.assets.push(obj);
    }
})();
