sys_script.script:
(function executeRule(current, previous) {

    try {

        // -------------------------------
        // 1. Read and validate quantities
        // -------------------------------
        var newQty = parseInt(current.quantity, 10) || 0;
        var oldQty = parseInt(previous.quantity, 10) || 0;

        // Calculate only the delta
        var qtyDifference = newQty - oldQty;

        // If quantity is not increased, do nothing
        if (qtyDifference <= 0) {
            return;
        }

        // -------------------------------
        // 2. Get Asset Catalog record
        // -------------------------------
        var catalogId = current.request_asset + '';
        if (!catalogId) {
            gs.error("ASSET BR: No catalog reference found");
            return;
        }

        var catalogGR = new GlideRecord('x_1862743_asset_re_asset_catalog_table');
        if (!catalogGR.get(catalogId)) {
            gs.error("ASSET BR: Catalog record not found: " + catalogId);
            return;
        }

        // -------------------------------
        // 3. Check available quantity
        // -------------------------------
        var availableQty = parseInt(catalogGR.available_quantity, 10) || 0;

        gs.info(
            "ASSET BR DEBUG | Available Before: " + availableQty +
            " | Qty Difference: " + qtyDifference
        );

        if (availableQty < qtyDifference) {
            gs.addErrorMessage("Insufficient stock available in asset catalog.");
            current.setAbortAction(true);
            return;
        }

        // -------------------------------
        // 4. Update available quantity
        // -------------------------------
        catalogGR.available_quantity = availableQty - qtyDifference;
        catalogGR.update();

        gs.info(
            "ASSET BR DEBUG | Available After: " +
            catalogGR.available_quantity
        );

    } catch (e) {
        gs.error("ASSET BR ERROR: " + e.message);
    }

})(current, previous);
