sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // If the form is loading or the quantity is empty/zero, stop here.
    if (isLoading || newValue === '' || newValue == 0) {
        return;
    }

    var quantity = parseInt(newValue);

    // 1. Reference the 'request_asset' field to look up data from the Catalog table
    g_form.getReference('request_asset', function(assetRecord) {

        // 2. Pull the 'unit_cost' from the Asset Catalog table
        var unitCost = parseFloat(assetRecord.unit_cost);

        if (!isNaN(unitCost)) {
            // 3. Formula: Total Cost = Quantity * Unit Cost
            var total = quantity * unitCost;

            // 4. Update the 'total_cost' field on the Request form immediately
            g_form.setValue('total_cost', total);
        }
    });
}
